def backfill_micronutrient_targets(apps, schema_editor):
    """
    Backfill micronutrient targets for existing NutritionTargets records.
    Only updates targets that:
    1. Have empty or missing micronutrients field
    2. Are not custom (is_custom=False)
    3. Have associated user metrics
    """
    NutritionTargets = apps.get_model('accounts', 'NutritionTargets')
    UserMetrics = apps.get_model('accounts', 'UserMetrics')
    
    # Import the function (using the actual module path)
