    from project.utils.nutrition_calculator import calculate_micronutrient_targets
    
    # Find all nutrition targets that need updating
    targets_to_update = NutritionTargets.objects.filter(
        is_custom=False
    ).select_related('user')
    
    updated_count = 0
    skipped_count = 0
    
    for target in targets_to_update:
        # Check if micronutrients is empty or missing
        if not target.micronutrients or target.micronutrients == {}:
            try:
                # Try to get user metrics
                metrics = UserMetrics.objects.get(user=target.user)
                
                # Calculate micronutrient targets
                micronutrient_targets = calculate_micronutrient_targets(
                    metrics.age,
                    metrics.gender
                )
                
                # Update the target
                target.micronutrients = micronutrient_targets
                target.save(update_fields=['micronutrients'])
                updated_count += 1
            except UserMetrics.DoesNotExist:
                # User doesn't have metrics, skip
                skipped_count += 1
                continue
            except Exception as e:
                # Log error but continue
                print(f"Error updating targets for user {target.user.id}: {e}")
                skipped_count += 1
                continue
    
    print(f"Migration completed: {updated_count} targets updated, {skipped_count} skipped")


